package metier;

public class Calcul implements Icalcul{

	@Override
	public double addition(double a, double b) {
		// TODO Auto-generated method stub
		return (a+b);
	}

	@Override
	public double soust(double a, double b) {
		// TODO Auto-generated method stub
		return (a-b);
	}

	@Override
	public double produit(double a, double b) {
		// TODO Auto-generated method stub
		return (a*b);
	}
	  
	

}
